import React, { useState } from 'react'
import {Chart as ChartJs, 
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js'
import { DatePicker } from 'antd'
import {Line} from 'react-chartjs-2'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'
import { dateFormat } from '../../utils/dateFormat'

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
)

function Chart() {

    const {incomes, expenses} = useGlobalContext()
    incomes.sort(function(a,b){
        let d1=new Date(a.date),d2=new Date(b.date)
        if(d1>d2)
        {
            return 1
        }
        else
        return -1
    })
    // console.log(incomes)
    const data = {
        labels: incomes.map((inc) =>{
            const {date} = inc
            // console.log(incomes)
            return dateFormat(date)
        }),
        datasets: [
            {
                label: 'Income',
                data: [
                    incomes.map((income) => {
                        const {amount} = income
                        // console.log()
                        return amount
                    })
                ],
                backgroundColor: 'green',
                tension: .2
            },
            // {
            //     label: 'Expenses',
            //     data: [
            //         ...expenses.map((expense) => {
            //             const {amount} = expense
            //             return amount
            //         })
            //     ],
            //     backgroundColor: 'red',
            //     tension: .2
            // }
        ]
    }
    const config = {
        type: 'bar',
        data,
        options: {
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      };

      // render init block
    //   const myChart = new Chart(
    //     document.getElementById('myChart'),
    //     config
    //   );
    // 
    function filter()
    {
        const ele=document.getElementById('month')
        if(ele.value!=null)
        {console.log(ele.value)}
    }

    return (
        <ChartStyled >
            <div><input type='month' id='month' onChange={filter}/></div>
            <Line data={data} />            
        </ChartStyled>
    )
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    // width:110%;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    div{
        text-align:center;
        margin-top:20px;
    }
    #startdate{
        text-align:center;
    }
`;

export default Chart
