import React, { useState,useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';
import {Pie} from 'react-chartjs-2'
function Loan() {
    const [principal, setPrincipal] = useState(0);
    const [interest, setInterest] = useState(0);
    const [years, setYears] = useState(0);
    const [emi, setEMI] = useState(0);
    const [totAmount, settotAmount] = useState(0);
    const handleChange = (e) => {
        console.log(e.target.id, e.target.value);
        const id = e.target.id;
        const value = parseInt(e.target.value);
        if (id === 'principal') {
            setPrincipal(value);
        } else if (id === 'interest') {
            setInterest(value);
        } else {
            setYears(value);
        }
    }
    // P(r(1+r)^n/((1+r)^n)-1))
    const calculate = () => {
        let r = interest;
        if (principal && r && years) {
            r = r / 12 / 100; // per month
            const calcPow = Math.pow(1 + r, years * 12);
            const emii = principal * ((r * calcPow) / (calcPow - 1));
            const tamount=emii*years*12;
            settotAmount(Math.round(tamount));
            setEMI(Math.round(emii));
        }
    }

    useEffect(() => {
        calculate();
    }, [principal, interest, years])

    return (
        <LoanStyled>
            <InnerLayout>
                <div className='loan-calc'>
                    <h1>Mortgage Caclulator</h1>

                    <div className='inputs'>
                        <p>Principal:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='principal' />

                        <p>Interest:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='interest' />

                        <p>Years:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='year' />
                    </div>

                    {/* <div className='output'>
                        EMI(Monthly) is {emi}
                    </div> */}
                    <div className='output'>
                        Total Interest is {totAmount-principal}
                    </div>
                    <div className='output'>
                        Your Total Payment(Loan Amount + Interest) is {totAmount}
                    </div>
                </div>
                <div className='container'>
                <div className="output">
                    <div>EMI(Monthly)</div>
                    <div>{emi}</div>
{/* 
                        <p style={{
                            color: type === 'expense' ? 'red' : 'var(--color-green)'
                        }}>
                            {
                                type === 'expense' ? `-${amount <= 0 ? 0 : amount}` : `+${amount <= 0 ? 0: amount}`
                            }
                        </p> */}
                    </div>
                    </div>
                <Pie
                    data={
                        {
                            labels:['Total Interest','Total Amount'],
                            datasets:[{
                                data:[totAmount-principal,totAmount],
                                backgroundColor:['yellow','blue']
                            }]
                        }
                    }
                    width={200}
                    height={200}
                />
            </InnerLayout>
        </LoanStyled>
    )
}

const LoanStyled = styled.div`
.container{
    display:flex;
    width:100%;
    justify
}
    .output{
        width:60%;
            background: #FCF6F9;
            border: 2px solid #FFFFFF;
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            padding: 1rem;
            border-radius: 20px;
            display: flex;
            justify-content: space-between;
            align-items: center;
        }
    }
    
`;

export default Loan
