import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';
import IncomeItem from '../IncomeItem/IncomeItem';
function ViewTransaction() {
    const {totalExpenses,incomes, expenses, totalIncome,addIncome,deleteIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext()

    const user=JSON.parse(localStorage.getItem('user'))

    useEffect(() => {
        getIncomes()
        getExpenses()
    }, [])
    incomes.sort(function(a,b){
        let d1=new Date(a.date),d2=new Date(b.date)
        if(d1>d2)
        {
            return 1
        }
        else
        return -1
    })
    return (
        <ViewTransactionStyled>
            <InnerLayout>

            <div className="incomes">
                        {incomes.map((income) => {
                            const {_id, amount, date, category, description, type} = income;
                            return <IncomeItem
                                key={_id}
                                id={_id} 

                                description={description} 
                                amount={amount} 
                                date={date} 
                                type={type}
                                category={category} 
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteIncome}
                            />
                        })}
                    </div>
                    
            </InnerLayout>
        </ViewTransactionStyled>
    )
}

const ViewTransactionStyled = styled.div`
    .stats-con{
        display: flex;
        justify-content:center;
        
        gap: 2rem;
        .chart-con{
            grid-column: 1 / 4;
            height: 400px;
            .amount-con{
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;
                .income, .expense{
                    grid-column: span 2;
                }
                .income, .expense, .balance{
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    p{
                        font-size: 2rem;
                        font-weight: 700;
                    }
                }

                .balance{
                    grid-column: 2 / 4;
                    margin-bottom:10px;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    p{
                        color: var(--color-green);
                        opacity: 0.6;
                        font-size: 2rem;
                    }
                }
            }
        }

        .history-con{
            grid-column: 4 / -1;
            h2{
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }
            .salary-title{
                font-size: 1.2rem;
                span{
                    font-size: 1.8rem;
                }
            }
            .salary-item{
                background: #FCF6F9;
                border: 2px solid #FFFFFF;
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                padding: 1rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                p{
                    font-weight: 600;
                    font-size: 1.6rem;
                }
            }
        }
    }
`;

export default ViewTransaction
