import React, { useState,useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';
import {Pie} from 'react-chartjs-2'
function Loan() {
    const [principal, setPrincipal] = useState(0);
    const [interest, setInterest] = useState(0);
    const [years, setYears] = useState(0);
    const [emi, setEMI] = useState(0);
    const [totAmount, settotAmount] = useState(0);
    const handleChange = (e) => {
        console.log(e.target.id, e.target.value);
        const id = e.target.id;
        const value = parseInt(e.target.value);
        if (id === 'principal') {
            setPrincipal(value);
        } else if (id === 'interest') {
            setInterest(value);
        } else {
            setYears(value);
        }
    }

    // P(r(1+r)^n/((1+r)^n)-1))
    const calculate = () => {
        let r = interest;
        if (principal && r && years) {

            r = r / 12 / 100; // per month
            const calcPow = Math.pow(1 + r, years * 12);
            const emii = principal * ((r * calcPow) / (calcPow - 1));
            const tamount=emii*years*12;
            settotAmount(Math.round(tamount));
            setEMI(Math.round(emii));
        }
    }

    useEffect(() => {
        calculate();
    }, [principal, interest, years])

    return (
        <LoanStyled>
            <InnerLayout>
                <div className='loan-calc'>
                    <h1>Mortgage Caclulator</h1>

                    <div className='inputs'>
                        <p>Principal:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='principal' />

                        <p>Interest:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='interest' />

                        <p>Years:</p>
                        <input
                            onChange={handleChange}
                            type='number' id='year' />
                    </div>

                    <div className='output'>
                        EMI(Monthly) is {emi}
                    </div>
                    <div className='output'>
                        Total Interest is {totAmount-principal}
                    </div>
                    <div className='output'>
                        Your Total Payment(Loan Amount + Interest) is {totAmount}
                    </div>
                </div>
                <Pie>
                    data
                </Pie>
            </InnerLayout>
        </LoanStyled>
    )
}

const LoanStyled = styled.div`
    .stats-con{
        display: flex;
        justify-content:center;
        
        gap: 2rem;
        .chart-con{
            grid-column: 1 / 4;
            height: 400px;
            .amount-con{
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;
                .income, .expense{
                    grid-column: span 2;
                }
                .income, .expense, .balance{
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    p{
                        font-size: 2rem;
                        font-weight: 700;
                    }
                }

                .balance{
                    grid-column: 2 / 4;
                    margin-bottom:10px;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    p{
                        color: var(--color-green);
                        opacity: 0.6;
                        font-size: 2rem;
                    }
                }
            }
        }

        .history-con{
            grid-column: 4 / -1;
            h2{
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }
            .salary-title{
                font-size: 1.2rem;
                span{
                    font-size: 1.8rem;
                }
            }
            .salary-item{
                background: #FCF6F9;
                border: 2px solid #FFFFFF;
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                padding: 1rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                p{
                    font-weight: 600;
                    font-size: 1.6rem;
                }
            }
        }
    }
`;

export default Loan
